const langs=["az","hy","be","ka","kk","ky","lv","lt","mo","ru","tk","uk","et"],userLang=navigator.language.substring(0,2)||navigator.userLanguage.substring(0,2),isRu=a=>userLang===a;if(localStorage.getItem("lang")){if("en"===localStorage.getItem("lang")){const a=window.location.pathname.split(".");window.location=`${a[0]}en.html`}}else langs.some(isRu)||(window.location=`${window.location}en.html`);const setLanguage=a=>{document.querySelectorAll("[data-lang]").forEach(t=>{a.target===t&&localStorage.setItem("lang",t.dataset.lang)})};document.addEventListener("click",setLanguage);